mapper.select:
SELECT DISTINCT cateCode FROM vam_book WHERE






						authorId = #{authorId}





			bookName LIKE CONCAT('%',CONCAT(#{keyword},'%'))

	SELECT DISTINCT COUNT(*) cateCount,b.cateCode,c.cateName
	FROM vam_book b LEFT OUTER JOIN vam_bcate c
	ON b.cateCode = c.cateCode
	WHERE






						authorId = #{authorId}



			AND b.cateCode = #{cateCode}


			bookName LIKE '%' || #{keyword} || '%' AND b.cateCode = #{cateCode}



GROUP BY b.cateCode,c.CateName
