mapper.select:
SELECT replyId
FROM vam_reply
WHERE memberId= #{memberId} AND bookId = #{bookId}


SELECT * FROM
	(
		SELECT ROWNUM as rn, replyId, bookId, memberId, content, rating, regDate
		FROM vam_reply
		WHERE 
			ROWNUM <= #{pageNum} * #{amount}
			AND bookId = #{bookId}
		ORDER BY regDate DESC 
	)
WHERE rn > (#{pageNum}-1) * #{amount}

SELECT COUNT(*)
FROM vam_reply
WHERE bookId = #{bookId}
